import { create } from "zustand";

export let useStore = create((set, get) => ({
    history: [],
    vectors: {},
    links: [], // [[a,b,color], [],...]
    cloud: {},
    id: 0,
    gens: [
        {
            id: 0,
            texture:
               "data:image/png;base64,",
            map: "data:image/png;base64,",
            palette: [
                "rgb(0,0,0)",
                "rgb(50,50,50)",
                "rgb(100,100,100)",
                "rgb(150,150,150)",
                "rgb(200,200,200)",
            ],
            compass: { type: "", angles: [20, 40, 60, 80, 100] },
            multiposition: Array(512).fill(0.8),
            position: [0, 0, 0],
        },
    ],

    x: 0,
    fn: async (a) => {
        const x = get().x;

        set((state) => ({
            x: x + a,
        }));
    },

    newLink: (link) => {
        const links = [...get().links, link];
        set((state) => ({
            links,
        }));
    },

    calcNewId: () => {
        return get().gens.length;
    },

    newPick: async (pos512, pos3d, color, vector) => {
        const generateWithVector = get().generateWithVector;
        const gen = await generateWithVector(pos512, pos3d, color, vector); // INSERIRE CHIAMATA API QUI - inputs: pos512 & (pos3D) &vector

        const newId = get().calcNewId();
        gen.id = newId;

        console.log(newId);
        const gens = [...get().gens, gen];
        set((state) => ({
            gens,
            id: newId,
        }));
    },

    cloudPick: async (idCloud) => {
        const generateByCloudId = get().generateByCloudId;
        const gen = await generateByCloudId(idCloud); // INSERT API CALL HERE - inputs: idCloud512

        const newId = get().calcNewId();
        gen.id = newId;
        console.log(newId);
        
        const gens = [...get().gens, gen];
        get().removeCloudDot(idCloud);
        set((state) => ({
            gens,
            id: newId,
        }));
    },

    removeCloudDot: (idCloud) => {
        const cloud = get().cloud;
        const [key, id] = idCloud.split("/");
        cloud[key].splice(id, 1);
        set((state) => ({
            cloud,
        }));
    },

    retroPick: async (idImgs) => {
        set((state) => ({
            id: idImgs,
        }));
    },

    // test functions for local dev ---------------------------------------------------------------------------------------------------------------------
    generateWithVector: async (pos512, pos3d, color, vector) => {
        // Replace the following with API call values ↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓
        const response = await fetch("http://130.60.24.190/get-image", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify([color, pos512]),
            });
        const schema = await response.json();
           
        // Add info on 3Dspace
        schema.position = pos3d.map((p, i) => p + vector[i]);
        return schema;
    },

    generateByCloudId: async (idCloud) => {
        // Add info on 3Dspace
        const [key, id] = idCloud.split("/");
        // Replace the following with API call values ↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓↓
        const response = await fetch("http://130.60.24.190/get-index", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify([key, id]),
            });
        const schema = await response.json();
        const cloud = get().cloud;
        
        schema.position = cloud[key][id];
        return schema;
    },
}));
